mapper.select:
select

from billing

  and id = #{id}
  and parking_id = #{parkingId}
  and free_time = #{freeTime}
  and paid_free_time = #{paidFreeTime}
  and wechat_discount = #{wechatDiscount}
  and alipay_discount = #{alipayDiscount}
  and day_cost = #{dayCost}



    order by ${sort} ${order}


    order by id desc



  limit #{offset}, #{limit}

select count(id) from billing

  and id = #{id}
  and parking_id = #{parkingId}
  and free_time = #{freeTime}
  and paid_free_time = #{paidFreeTime}
  and wechat_discount = #{wechatDiscount}
  and alipay_discount = #{alipayDiscount}
  and day_cost = #{dayCost}
