mapper.insert:
insert into business_transaction_record (id, user_id, `type`, 
  car_number, amount, balance, create_time, free_time
  )
values (#{id,jdbcType=BIGINT}, #{userId,jdbcType=BIGINT}, #{type,jdbcType=TINYINT}, 
  #{carNumber,jdbcType=VARCHAR}, #{amount,jdbcType=DECIMAL}, #{balance,jdbcType=DECIMAL}, #{createTime,jdbcType=TIMESTAMP}, #{freeTime,jdbcType=INTEGER}
  )

mapper.select:
select

from business_transaction_record

  and user_id = #{userId}



    order by ${sort} ${order}


    order by create_time desc



  limit #{offset}, #{limit}
