select

from car

  and id = #{id}
  and number = #{number}
  and parking_id = #{parkingId}
  and parking_type = #{parkingType}
  and monthly_parking_start = #{monthlyParkingStart}
  and monthly_parking_end = #{monthlyParkingEnd}
  and status = #{status}
  and infield_permission = #{infieldPermission}
  and create_id = #{createId}
  and create_time = #{createTime}
  and update_time = #{updateTime}
  and name = #{name}
  and phone = #{phone}
  and corporate_name = #{corporateName}
  and remarks = #{remarks}
  and classification = #{classification}
  and time_coupon = #{timeCoupon}
  and uuid = #{uuid,jdbcType=VARCHAR}

    AND (number LIKE CONCAT('%',#{keyword},'%'))


    and parking_id in

      #{parkingId}





    order by create_time desc , ${sort} ${order}


    order by create_time desc



  limit #{offset}, #{limit}

